knowledge_graph_schema:
type GDPGrowth @node {
  year: String
  totalImportExportAmount: String
  exportTotalAmount: String
  importTotalAmount: String
}

type TradeWarResolution @node {
  measures: String
}

type ImportSubstitutionStrategy @node {
  strategyDetails: String
}

type ServiceIndustryDevelopment @node {
  year: String
  totalServiceTradeAmount: String
  serviceTradeGrowthRate: String
  serviceExportAmount: String
  serviceExportGrowthRate: String
  serviceImportAmount: String
  serviceImportGrowthRate: String
  serviceTradeDeficit: String
  serviceTradeDeficitGrowthRate: String
}

type TransnationalCompanyStrategy @node {
  strategyDetails: String
}

type TechnologyTransferRestrictions @node {
  restrictionsDetails: String
}

type InternationalMarketShare @node {
  year: String
  chinaExportInternationalMarketShare: String
  consecutiveYearsAsGlobalLeader: String
}

type MarketAccessRules @node {
  rulesDetails: String
}

type LogisticsCosts @node {
  costDetails: String
}

type ImpactOfUSChinaTariffWarOnChinas2025ForeignTrade @node {
  gdpGrowth: [GDPGrowth] @relationship(type: "HAS_GDP_GROWTH", direction: OUT)
  tradeWarResolution: [TradeWarResolution] @relationship(type: "HAS_TRADE_WAR_RESOLUTION", direction: OUT)
  importSubstitutionStrategy: [ImportSubstitutionStrategy] @relationship(type: "HAS_IMPORT_SUBSTITUTION_STRATEGY", direction: OUT)
  serviceIndustryDevelopment: [ServiceIndustryDevelopment] @relationship(type: "HAS_SERVICE_INDUSTRY_DEVELOPMENT", direction: OUT)
  transnationalCompanyStrategy: [TransnationalCompanyStrategy] @relationship(type: "HAS_TRANSNATIONAL_COMPANY_STRATEGY", direction: OUT)
  technologyTransferRestrictions: [TechnologyTransferRestrictions] @relationship(type: "HAS_TECHNOLOGY_TRANSFER_RESTRICTIONS", direction: OUT)
  internationalMarketShare: [InternationalMarketShare] @relationship(type: "HAS_INTERNATIONAL_MARKET_SHARE", direction: OUT)
  marketAccessRules: [MarketAccessRules] @relationship(type: "HAS_MARKET_ACCESS_RULES", direction: OUT)
  logisticsCosts: [LogisticsCosts] @relationship(type: "HAS_LOGISTICS_COSTS", direction: OUT)
}

